import { Image, SafeAreaView, Text, View } from "react-native";
import Ionicons from "react-native-vector-icons/Ionicons";
import { COLORS } from "../../../constants";
import { formatCurrencyOnCard } from "../../../utils/currency-util";
import CustomButton from "../../../components/custom/Button";

export default function RoomCard(props) {
    const { navigation } = props
    const currentRoom = props.currentRoom

    return (
        <SafeAreaView style={{ flex: 1, backgroundColor: "#fff" }}>
            <View
                style={{
                    borderWidth: 1,
                    borderColor: "#ccc",
                    height: 100,
                    flexDirection: "row",
                    backgroundColor: "#fbfbfb",
                    borderRadius: 4
                }}
            >
                <View
                    style={{
                        width: 90,
                        height: 90,
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        marginTop: 4,
                        marginLeft: 4,
                        borderRadius: 4
                    }}
                >
                    <Image
                        style={{
                            width: "100%",
                            height: "100%",
                            resizeMode: "stretch",

                        }}
                        source={{
                            uri: currentRoom?.thubnailImage,
                        }}
                    />
                </View>
                <View
                    style={{
                        marginLeft: 8,
                        marginTop: 4,
                        width: 240
                    }}>
                    <Text
                        numberOfLines={1}
                        style={{
                            marginTop: 2,
                            fontSize: 11,
                            fontWeight: "bold"
                        }}
                    >
                        {currentRoom?.title}</Text>
                    <View
                        style={{
                            flexDirection: "row",
                        }}>
                        <View
                            style={{
                                width: 187
                            }}>
                            <Text
                                numberOfLines={2}
                                style={{
                                    fontSize: 11,
                                    marginTop: 4,
                                    lineHeight: 11

                                }}>{currentRoom?.address}</Text>
                            <View
                                style={{
                                    flexDirection: "row",
                                    display: "flex",
                                    justifyContent: "space-between",
                                    marginTop: 6
                                }}
                            >
                                <View
                                    style={{
                                        width: 98,
                                        maxWidth: 96
                                    }}>
                                    <View style={{ flexDirection: "row" }}>
                                        <Ionicons size={16} color={COLORS.primary} name="home-outline"></Ionicons>
                                        <Text style={{ fontSize: 11, marginTop: 2, marginLeft: 2 }}>{currentRoom?.roomType}</Text>
                                    </View>
                                    <View style={{ flexDirection: "row", marginTop: 6 }}>
                                        <Ionicons size={16} color={COLORS.primary} name="expand-outline"></Ionicons>
                                        <Text style={{ fontSize: 11, marginTop: 2, marginLeft: 2 }}>{currentRoom?.roomArea}mÂ²</Text>
                                    </View>
                                </View>
                                <View
                                    style={{
                                        marginRight: 20
                                    }}
                                >
                                    <View style={{ flexDirection: "row" }}>
                                        <Ionicons size={16} color={COLORS.primary} name="calendar-outline"></Ionicons>
                                        <Text style={{ fontSize: 11, marginTop: 2, marginLeft: 2 }}>{currentRoom?.paymentDate}-{(new Date().getMonth() - 1)}-{(new Date().getFullYear())}</Text>

                                    </View>
                                    <View style={{ flexDirection: "row", marginTop: 6 }}>
                                        <Ionicons size={16} color={COLORS.primary} name="clipboard-outline"></Ionicons>
                                        <Text style={{ fontSize: 11, marginTop: 2, marginLeft: 2 }}>{currentRoom?.paymentStatus === "WAITING_CREATE_BILL" ? "Not created yet" : currentRoom?.paymentStatus}</Text>
                                    </View>
                                </View>
                            </View>
                        </View>
                        <View
                            style={{
                                width: 66,
                                display: "flex",
                                alignItems: "center",
                                marginLeft: 10
                            }}>
                            <Text
                                style={{
                                    color: COLORS.primary,
                                    fontSize: 32,
                                    fontWeight: "bold",
                                    lineHeight: 42
                                }}
                            >{formatCurrencyOnCard(currentRoom?.rentalPrice)}</Text>
                            <Text style={{ fontSize: 11, color: COLORS.primary, lineHeight: 12, marginTop: -6, marginBottom: 2 }}>Milions</Text>
                                <CustomButton
                                    disabled={currentRoom?.paymentStatus !== "NOT_YET"}
                                    label={"Payment"}
                                    fontSize={11}
                                    paddingVertical={6}
                                    paddingHorizontal={6}
                                    onPress={() => navigation.navigate("PaymentMonthly", {
                                        billId: currentRoom.billId
                                    })}
                                ></CustomButton>
                        </View>
                    </View>
                </View>


            </View>
        </SafeAreaView>
    )
}
