            isOpen={showDepositHolderModal}
            onClose={() => setShowDepositHolderModal(false)}
            size="xl"
          >
            <Modal.Content maxWidth="350">
              <Modal.CloseButton />
              <Modal.Header>
                <Text
                  style={{ fontSize: 16, textAlign: "center", paddingTop: 3 }}
                >
