              </Modal.Header>
              <Modal.Body>
                {depositOptionsData.map((depositOption) => {
                  return (
                    <TouchableOpacity
                      style={{
                        flexDirection: "row",
                        alignItems: "center",
                        padding: 16,
                      }}
                      key={depositOption?.id}
                      onPress={() => changeDepositOptionCheckbox(depositOption)}
                    >
                      <Checkbox
                        style={{
                          width: 16,
                          height: 16,
                          marginEnd: 20,
                        }}
                        color={COLORS.primary}
                        value={depositOption?.isChecked}
                        onValueChange={() =>
                          changeDepositOptionCheckbox(depositOption)
                        }
                      />
                      <Text
                        style={{
                          fontSize: 14,
                          fontFamily: FONTS.regular,
                          color: depositOption?.isChecked
                            ? COLORS.primary
                            : COLORS.textDefault,
                        }}
                      >
                        {depositOption?.id === 1
                          ? `${currencyViCode(
                              Number(roomDetailData?.deposit) / 2
                            ).replace(/.$/, "VND")} ${depositOption?.title}`
                          : depositOption?.title}
                      </Text>
                    </TouchableOpacity>
                  );
                })}
              </Modal.Body>
              <Modal.Footer>
                <CustomButton
                  flex={1}
                  label="Deposit"
                  fontSize={14}
                  onPress={depositRoom}
                />
              </Modal.Footer>
            </Modal.Content>
          </Modal>
          {/* FOOTER */}
          {!innkeeperRoom && (
            <View
              style={{
                flexDirection: "row",
                justifyContent: `${
                  route?.params?.roomStatus ? `center` : `space-around`
                }`,
                alignItems: "center",
                paddingVertical: 10,
                paddingHorizontal: 8,
              }}
            >
              {route?.params?.roomStatus ? (
                <CustomButton
                  label="View Contract"
                  backgroundColor={COLORS?.primary}
                  onPress={() =>
                    navigation.navigate("PreviewContract", {
                      contractId: contract?.id,
                    })
                  }
                />
              ) : (
                <>
                  <View
                    style={{
                      paddingHorizontal: 16,
                      paddingVertical: 14,
                    }}
                  >
                    <Text
                      style={{
                        textAlign: "center",
                        fontFamily: FONTS.medium,
                        fontSize: 16,
                        color: "#fafafa",
                      }}
                    >
                      <Feather name="phone-call" size={17} color="#fafafa" />
