import { Dimensions } from "react-native";

export const SLIDER_WIDTH = Dimensions.get("window").width + 80;
export const ITEM_WIDTH_HOME = Math.round(SLIDER_WIDTH * 0.7);
export const HEIGHT_HOME = 160;
export const ITEM_WIDTH_DETAIL = Math.round(SLIDER_WIDTH);
export const HEIGHT_DETAIL = 260;
